mapper.insert:
insert into OAAS_PRIV_DATA_GROUP (DATA_GROUP_ID, DATA_GROUP_NAME, DATA_GROUP_CODE
  )
values (#{dataGroupId,jdbcType=DECIMAL}, #{dataGroupName,jdbcType=VARCHAR}, #{dataGroupCode,jdbcType=VARCHAR}
  )

mapper.update:
update OAAS_PRIV_DATA_GROUP
set DATA_GROUP_NAME = #{dataGroupName,jdbcType=VARCHAR},
  DATA_GROUP_CODE = #{dataGroupCode,jdbcType=VARCHAR}
where DATA_GROUP_ID = #{dataGroupId,jdbcType=DECIMAL}

mapper.select:
SELECT OPDG.DATA_GROUP_ID,OPDG.DATA_GROUP_NAME,OPDG.DATA_GROUP_CODE FROM OAAS_PRIV_DATA_GROUP AS OPDG
INNER JOIN OAAS_USER_DATA_GRP AS OUDG ON OUDG.DATA_GROUP_ID =OPDG.DATA_GROUP_ID
OUDG.USER_ID = #{userId,jdbcType=DECIMAL}
LIMIT #{start}, #{limit}

SELECT COUNT(1) FROM OAAS_PRIV_DATA_GROUP AS OPDG
INNER JOIN OAAS_USER_DATA_GRP AS OUDG ON OUDG.DATA_GROUP_ID =OPDG.DATA_GROUP_ID
OUDG.USER_ID = #{userId,jdbcType=DECIMAL}

SELECT OPDG.DATA_GROUP_ID,OPDG.DATA_GROUP_NAME,OPDG.DATA_GROUP_CODE FROM OAAS_PRIV_DATA_GROUP AS OPDG
INNER JOIN OAAS_USER_DATA_GRP AS OUDG ON OUDG.DATA_GROUP_ID =OPDG.DATA_GROUP_ID
${whereSql}
ORDER BY ${orderBySql}
ORDER BY DATA_GROUP_ID DESC
