mapper.insert:
insert into OAAS_PRIV_DATA_INST (DATA_INST_ID, DATA_ID, DATA_INST_NAME
  )
values (#{dataInstId,jdbcType=DECIMAL}, #{dataId,jdbcType=DECIMAL}, #{dataInstName,jdbcType=VARCHAR}
  )

mapper.update:
update OAAS_PRIV_DATA_INST
set DATA_ID = #{dataId,jdbcType=DECIMAL},
  DATA_INST_NAME = #{dataInstName,jdbcType=VARCHAR}
where DATA_INST_ID = #{dataInstId,jdbcType=DECIMAL}

mapper.select:
  	SELECT 
  		OPDI.DATA_INST_ID, OPDI.DATA_ID, OPDI.DATA_INST_NAME,
  		OPD.DATA_NAME,OPD.DATA_CODE,OPDST.SCOPE_TYPE_NAME,OPDST.DATA_URL
  	FROM OAAS_PRIV_DATA_INST OPDI
  	LEFT JOIN OAAS_PRIV_DATA AS OPD ON OPD.DATA_ID = OPDI.DATA_ID
	LEFT JOIN OAAS_PRIV_DATA_SCOPE_TYPE AS OPDST ON OPD.SCOPE_TYPE = OPDST.SCOPE_TYPE

	INNER JOIN OAAS_USER_DATA_INST OUDI ON OUDI.DATA_INST_ID =OPDI.DATA_INST_ID



  			OPDI.DATA_ID = #{dataId,jdbcType=DECIMAL}


			OUDI.USER_ID =#{userId,jdbcType=DECIMAL}



      LIMIT #{start}, #{limit}

SELECT OPDI.*, OPD.DATA_NAME,OPD.DATA_CODE,OPDST.SCOPE_TYPE_NAME,OPDST.DATA_URL FROM OAAS_PRIV_DATA_INST OPDI LEFT JOIN OAAS_PRIV_DATA AS OPD ON OPD.DATA_ID = OPDI.DATA_ID LEFT JOIN OAAS_PRIV_DATA_SCOPE_TYPE AS OPDST ON OPD.SCOPE_TYPE = OPDST.SCOPE_TYPE
OPD.STATE = '10A'
AND ${whereSql}
ORDER BY ${orderBySql}
ORDER BY OPDI.DATA_INST_ID DESC
