mapper.select:
SELECT * FROM (SELECT OPD.DATA_ID,OPD.DATA_CODE,OPD.DATA_NAME, OPD.MODIFY_USER_ID,OPD.MODIFY_TIME,OPD.REMARKS, OPD.SCOPE_TYPE,OPD.STATE,OPD.CREATE_TIME, OPD.CREATE_USER_ID,OPDST.SCOPE_TYPE_NAME,'分组' AS DATA_SOURCE, OUDG.USER_ID AS USER_ID,OPDI.DATA_INST_ID FROM OAAS_PRIV_DATA AS OPD INNER JOIN OAAS_PRIV_DATA_INST AS OPDI ON OPDI.DATA_ID = OPD.DATA_ID INNER JOIN OAAS_PRIV_DATA_GRP_INST AS OPDGI ON OPDGI.DATA_INST_ID = OPDI.DATA_INST_ID INNER JOIN OAAS_USER_DATA_GRP AS OUDG ON OUDG.DATA_GROUP_ID = OPDGI.DATA_GROUP_ID INNER JOIN OAAS_PRIV_DATA_SCOPE_TYPE AS OPDST ON OPDST.SCOPE_TYPE = OPD.SCOPE_TYPE
UNION ALL
SELECT OPD.DATA_ID,OPD.DATA_CODE,OPD.DATA_NAME, OPD.MODIFY_USER_ID,OPD.MODIFY_TIME,OPD.REMARKS, OPD.SCOPE_TYPE,OPD.STATE,OPD.CREATE_TIME, OPD.CREATE_USER_ID,OPDST.SCOPE_TYPE_NAME, '单独赋权' AS DATA_SOURCE,OUDI.USER_ID AS USER_ID,OPDI.DATA_INST_ID FROM OAAS_PRIV_DATA AS OPD INNER JOIN OAAS_PRIV_DATA_INST AS OPDI ON OPDI.DATA_ID = OPD.DATA_ID INNER JOIN OAAS_PRIV_DATA_SCOPE_TYPE AS OPDST ON OPDST.SCOPE_TYPE = OPD.SCOPE_TYPE INNER JOIN OAAS_USER_DATA_INST AS OUDI ON OUDI.DATA_INST_ID = OPDI.DATA_INST_ID ) A
A.USER_ID = #{userId,jdbcType=DECIMAL}
LIMIT #{start}, #{limit}
