mapper.select:
SELECT RES.*, OP.PRIVATE_CODE, OP.PRIVATE_TYPE, OP.PRIVATE_NAME, OU.USER_TEXT 'createUserName' FROM ( SELECT ORP.*,MENU.MENU_ID 'menuId', MENU.OPEN_STATUS AS OPEN_STATUS FROM OAAS_PRIV_MENU MENU JOIN OAAS_ROLE_PRIVATE AS ORP ON MENU.PRIVATE_ID = ORP.PRIVATE_ID AND MENU.STATE = '10A' UNION ALL SELECT ORP.*,FUNC.MENU_ID 'menuId', FUNC.OPEN_STATUS AS OPEN_STATUS FROM OAAS_PRIV_FUNC FUNC JOIN OAAS_ROLE_PRIVATE AS ORP ON FUNC.PRIVATE_ID = ORP.PRIVATE_ID AND FUNC.STATE = '10A' UNION ALL SELECT ORP.*,ATTR.MENU_ID 'menuId', ATTR.OPEN_STATUS AS OPEN_STATUS FROM OAAS_PRIV_ATTR ATTR JOIN OAAS_ROLE_PRIVATE AS ORP ON ATTR.PRIVATE_ID = ORP.PRIVATE_ID AND ATTR.STATE = '10A' ) RES LEFT JOIN OAAS_PRIVATE AS OP ON RES.PRIVATE_ID = OP.PRIVATE_ID LEFT JOIN OAAS_USER OU ON OU.USER_ID = RES.CREATE_USER_ID
OP.STATE = '10A'
AND ${whereSql}
ORDER BY ${orderBySql}
ORDER BY RES.ROLE_PRIVATE_ID DESC
