mapper.update:
UPDATE OAAS_STAFF_JOB


    JOB_ID = #{jobId,jdbcType=DECIMAL},


    STAFF_ID = #{staffId,jdbcType=DECIMAL},


    DEFAULT_JOB = #{defaultJob,jdbcType=DECIMAL},


    SOURCE = #{source,jdbcType=NUMERIC}


WHERE STAFF_JOB_ID = #{staffJobId,jdbcType=DECIMAL}

UPDATE OAAS_STAFF_JOB
SET JOB_ID = #{jobId,jdbcType=DECIMAL},
  STAFF_ID = #{staffId,jdbcType=DECIMAL},
  DEFAULT_JOB = #{defaultJob,jdbcType=DECIMAL},
  SOURCE = #{source,jdbcType=NUMERIC}
WHERE STAFF_JOB_ID = #{staffJobId,jdbcType=DECIMAL}

mapper.select:
  	SELECT 
	    OSJ.*,
		OJ.JOB_NAME,
		OO.ORG_NAME
    FROM OAAS_STAFF_JOB AS OSJ
	LEFT JOIN OAAS_JOB AS OJ ON OJ.JOB_ID = OSJ.JOB_ID
	LEFT JOIN OAAS_ORG AS OO ON OO.ORG_ID = OJ.ORG_ID


    		AND OSJ.STAFF_ID = #{staffId,jdbcType=DECIMAL}


    		AND OSJ.JOB_ID = #{jobId,jdbcType=DECIMAL}


    		AND OSJ.DEFAULT_JOB = #{default,jdbcType=DECIMAL}



      LIMIT #{start}, #{limit}

SELECT COUNT(1) FROM OAAS_STAFF_JOB AS OSJ LEFT JOIN OAAS_JOB AS OJ ON OJ.JOB_ID = OSJ.JOB_ID LEFT JOIN OAAS_ORG AS OO ON OO.ORG_ID = OJ.ORG_ID
OSJ.STAFF_ID = #{staffId,jdbcType=DECIMAL}
AND OSJ.JOB_ID = #{jobId,jdbcType=DECIMAL}

  	SELECT 
	    COUNT(1)
    FROM OAAS_STAFF_JOB AS OSJ
    WHERE 
    STAFF_ID = #{staffId,jdbcType=DECIMAL}
    AND	JOB_ID = #{jobId,jdbcType=DECIMAL}

SELECT * FROM OAAS_STAFF_JOB


        ${whereSql}



    ORDER BY ${orderBySql}


    ORDER BY STAFF_JOB_ID DESC
